SELECT DATE_FORMAT(event_time, #{period}) as time, count(*) as num
FROM events


        event_time > #{startDate} and event_time < #{endDate}


        event_time > #{startDate}


        event_time < #{endDate}


GROUP BY time

        SELECT DATE_FORMAT(event_time, #{period}) as time, count(*) as num
        FROM events e
--         INNER JOIN venues v on v.venue_id = e.venue_id
        INNER JOIN groups g on g.group_id = e.group_id

            city_id = #{cityId}

                and event_time > #{startDate} and event_time < #{endDate}


                and event_time > #{startDate}


                and event_time < #{endDate}


        GROUP BY time
